<?phpclass ACCREDO_SB{	/**	 * This is a unique identifier of plugin.	 */	protected $plugin_name;	protected $version;	/**	 * The core functionality of the plugin.	 *	 */	public function __construct() {		if ( defined( 'ACCREDO_SB_VERSION' ) ) {			$this->version = ACCREDO_SB_VERSION;		} else {			$this->version = '1.0';		}		$this->plugin_name = 'wts-booking-center';		$this->load_dependencies();        $this->define_admin_hooks();	}	/**	 * Load the required dependencies.	 *	 * Include the required files:	 *	 */	private function load_dependencies() {        require_once plugin_dir_path( dirname( __FILE__ ) ) . 'admin/class-accredo-sb-admin.php';        require_once plugin_dir_path( dirname( __FILE__ ) ) . 'includes/class-accredo-sb-loader.php';		$this->loader = new ACCREDO_SB_Loader();			}	/**	 * Register admin hooks	 * of the plugin.	 *	 */	private function define_admin_hooks() {		$plugin_admin = new ACCREDO_SB_Admin( $this->get_plugin_name(), $this->get_version() );        $this->loader->add_filter( 'admin_menu', $plugin_admin,  'admin_menu' );        $this->loader->add_filter( 'admin_init', $plugin_admin,  'register_accredo_options' );        $this->loader->add_filter( 'cron_schedules', $plugin_admin,  'accredo_sb_car_feed' );        $this->loader->add_action( 'init', $plugin_admin, 'accredo_sb_get_shedule_car_feed' );        $this->loader->add_action( 'accredo_sb_get_reshedule', $plugin_admin, 'accredo_sb_get_reshedule_callback' );//        $this->loader->add_acti5;5on( 'init', $plugin_admin, 'accredo_sb_get_reshedule_callback1' );    }    /**     * Run the loader.     *     */    public function max_run() {        $this->loader->max_run();    }	/**	 * The name of the plugin	 *	 */	public function get_plugin_name() {		return $this->plugin_name;	}	/**	 * The version number of the plugin.	 */	public function get_version() {		return $this->version;	}}